sysauto_script.script:
// // Get today's time and date and calculate 24 hours from now
var rightNow = new GlideDateTime();

var twentyFourHoursFromNow = new GlideDateTime(rightNow);
twentyFourHoursFromNow.addDaysLocalTime(1);
//twentyFourHoursFromNow.addDaysUTC(1);
gs.info('Maintenant: ' + rightNow);
gs.info('TEMPS DEMAIN: ' + twentyFourHoursFromNow);
// Query the database for NeedIt Task records with Due date field values 
// between now and 24 hours from now. Only return NeedIt Task records that 
// do not have a State field value of Closed Complete.
var dueSoonNITasks = new GlideRecord('x_58872_needit_needit_task');
dueSoonNITasks.addQuery('due_date','>=',rightNow);
dueSoonNITasks.addQuery('due_date','<=',twentyFourHoursFromNow);
dueSoonNITasks.addQuery('state','!=',3);

dueSoonNITasks.query();

// Write a log message for each NeedIt Task Record due soon
while(dueSoonNITasks.next()){
  gs.info("NeedIt Task due within 24 hours: " + dueSoonNITasks.number);
  gs.eventQueue('x_58872_needit.dueSoonNeedItTasks',overdueNITasks,overdueNITasks.number,gs.getUserName());

}

